import { useEffect, useCallback } from 'react';
import { useSearchParams } from 'react-router-dom';
import { Place } from '../types/places';

// –ì–ª–æ–±–∞–ª—å–Ω–∞—è Map –¥–ª—è —Ö—Ä–∞–Ω–µ–Ω–∏—è —Å–æ—Å—Ç–æ—è–Ω–∏—è —Ñ–∏–ª—å—Ç—Ä–æ–≤ –≤ UI
if (!window.filterStates) {
  window.filterStates = new Map<string, boolean>();
}

export const useUrlFilters = (
  activeFilters: string[],
  onFilterChange: (filters: string[]) => void,
  filterCounts: Record<string, number>
) => {
  const [searchParams, setSearchParams] = useSearchParams();

  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–æ–¥—Å—á–µ—Ç–∞ –±–∞–∑–æ–≤—ã—Ö —Å—á–µ—Ç—á–∏–∫–æ–≤
  const calculateBaseCounts = (places: Place[]) => {
    console.log('üìä –ü–æ–¥—Å—á–µ—Ç –±–∞–∑–æ–≤—ã—Ö —Å—á–µ—Ç—á–∏–∫–æ–≤');
    const counts: Record<string, number> = {};

    places.forEach(place => {
      place.attributes.forEach(attr => {
        counts[attr] = (counts[attr] || 0) + 1;
      });
    });

    console.log('üìä –ë–∞–∑–æ–≤—ã–µ —Å—á–µ—Ç—á–∏–∫–∏:', counts);
    return counts;
  };

  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –ø–æ—Å–ª–µ–¥–æ–≤–∞—Ç–µ–ª—å–Ω–æ–π —Ñ–∏–ª—å—Ç—Ä–∞—Ü–∏–∏
  const applyFiltersSequentially = (
    places: Place[],
    filters: string[],
    baseCounts: Record<string, number>
  ) => {
    console.log('üîÑ –ü—Ä–∏–º–µ–Ω—è–µ–º —Ñ–∏–ª—å—Ç—Ä—ã –ø–æ—Å–ª–µ–¥–æ–≤–∞—Ç–µ–ª—å–Ω–æ:', filters);

    const result = {
      validFilters: [] as string[],
      counts: { ...baseCounts },
      filteredPlaces: places
    };

    for (const filter of filters) {
      console.log(`\nüìå –ü—Ä–æ–≤–µ—Ä—è–µ–º —Ñ–∏–ª—å—Ç—Ä: ${filter}`);

      // –§–∏–ª—å—Ç—Ä—É–µ–º –º–µ—Å—Ç–∞ –ø–æ —Ç–µ–∫—É—â–µ–º—É —Ñ–∏–ª—å—Ç—Ä—É
      const placesWithFilter = result.filteredPlaces.filter(
        place => place.attributes.includes(filter)
      );

      console.log(`üìä –ù–∞–π–¥–µ–Ω–æ –º–µ—Å—Ç: ${placesWithFilter.length}`);

      if (placesWithFilter.length > 0) {
        // –§–∏–ª—å—Ç—Ä –≤–∞–ª–∏–¥–µ–Ω
        console.log(`‚úÖ –§–∏–ª—å—Ç—Ä ${filter} –≤–∞–ª–∏–¥–µ–Ω`);
        result.validFilters.push(filter);
        result.filteredPlaces = placesWithFilter;

        // –ü–µ—Ä–µ—Å—á–∏—Ç—ã–≤–∞–µ–º —Å—á–µ—Ç—á–∏–∫–∏ –¥–ª—è –æ—Å—Ç–∞–≤—à–∏—Ö—Å—è —Ñ–∏–ª—å—Ç—Ä–æ–≤
        const remainingFilters = Object.keys(result.counts)
          .filter(attr => !result.validFilters.includes(attr));

        console.log('üîÑ –ü–µ—Ä–µ—Å—á–∏—Ç—ã–≤–∞–µ–º —Å—á–µ—Ç—á–∏–∫–∏ –¥–ª—è:', remainingFilters);

        remainingFilters.forEach(attr => {
          result.counts[attr] = placesWithFilter.filter(
            place => place.attributes.includes(attr)
          ).length;
        });
      } else {
        // –§–∏–ª—å—Ç—Ä –Ω–µ–≤–∞–ª–∏–¥–µ–Ω
        console.log(`‚ùå –§–∏–ª—å—Ç—Ä ${filter} –Ω–µ–≤–∞–ª–∏–¥–µ–Ω, –ø—Ä–æ–ø—É—Å–∫–∞–µ–º`);
        result.counts[filter] = 0;
      }
    }

    console.log('\nüìà –†–µ–∑—É–ª—å—Ç–∞—Ç —Ñ–∏–ª—å—Ç—Ä–∞—Ü–∏–∏:');
    console.log('–í–∞–ª–∏–¥–Ω—ã–µ —Ñ–∏–ª—å—Ç—Ä—ã:', result.validFilters);
    console.log('–ò—Ç–æ–≥–æ–≤—ã–µ —Å—á–µ—Ç—á–∏–∫–∏:', result.counts);

    return result;
  };

  // –û–±—Ä–∞–±–æ—Ç–∫–∞ –∏–∑–º–µ–Ω–µ–Ω–∏–π URL
  useEffect(() => {
    if (!window.places?.length) {
      console.log('‚è≥ –ñ–¥–µ–º –∑–∞–≥—Ä—É–∑–∫—É –º–µ—Å—Ç...');
      return;
    }

    const filtersFromUrl = searchParams.get('filter');
    console.log('\nüåê –ü–æ–ª—É—á–µ–Ω—ã —Ñ–∏–ª—å—Ç—Ä—ã –∏–∑ URL:', filtersFromUrl);

    // –°—á–∏—Ç–∞–µ–º –±–∞–∑–æ–≤—ã–µ —Å—á–µ—Ç—á–∏–∫–∏
    const baseCounts = calculateBaseCounts(window.places);

    if (!filtersFromUrl) {
      console.log('üßπ URL –ø—É—Å—Ç–æ–π, —Å–±—Ä–∞—Å—ã–≤–∞–µ–º —Ñ–∏–ª—å—Ç—Ä—ã');
      window.filterStates.clear();
      onFilterChange([]);
      return;
    }

    // –ü—Ä–∏–º–µ–Ω—è–µ–º —Ñ–∏–ª—å—Ç—Ä—ã –ø–æ—Å–ª–µ–¥–æ–≤–∞—Ç–µ–ª—å–Ω–æ
    const filters = filtersFromUrl.split(',');
    const { validFilters, counts } = applyFiltersSequentially(
      window.places,
      filters,
      baseCounts
    );

    // –ï—Å–ª–∏ –µ—Å—Ç—å –Ω–µ–≤–∞–ª–∏–¥–Ω—ã–µ —Ñ–∏–ª—å—Ç—Ä—ã, –æ–±–Ω–æ–≤–ª—è–µ–º URL
    if (validFilters.length !== filters.length) {
      console.log('üîÑ –û–±–Ω–æ–≤–ª—è–µ–º URL, —É–¥–∞–ª—è—è –Ω–µ–≤–∞–ª–∏–¥–Ω—ã–µ —Ñ–∏–ª—å—Ç—Ä—ã');
      if (validFilters.length > 0) {
        // –ü—Ä—è–º–æ–µ –∏–∑–º–µ–Ω–µ–Ω–∏–µ URL –±–µ–∑ –∫–æ–¥–∏—Ä–æ–≤–∞–Ω–∏—è
        const path = window.location.pathname;
        const newURL = `${path}?filter=${validFilters.join(',')}`;
        window.history.pushState({}, '', newURL);
      } else {
        // –£–¥–∞–ª—è–µ–º –ø–∞—Ä–∞–º–µ—Ç—Ä filter, –µ—Å–ª–∏ —Ñ–∏–ª—å—Ç—Ä–æ–≤ –Ω–µ –æ—Å—Ç–∞–ª–æ—Å—å
        const path = window.location.pathname;
        window.history.pushState({}, '', path);
      }
    }

    // –û–±–Ω–æ–≤–ª—è–µ–º UI —Å–æ—Å—Ç–æ—è–Ω–∏–µ
    console.log('üîÑ –û–±–Ω–æ–≤–ª—è–µ–º —Å–æ—Å—Ç–æ—è–Ω–∏—è —Ñ–∏–ª—å—Ç—Ä–æ–≤');
    window.filterStates.clear();
    validFilters.forEach(filter => {
      window.filterStates.set(filter, true);
    });

    onFilterChange(validFilters);
  }, [searchParams, window.places?.length]);

  // –û–±—Ä–∞–±–æ—Ç–∫–∞ –∫–ª–∏–∫–∞ –ø–æ —Ñ–∏–ª—å—Ç—Ä—É
  const toggleFilter = useCallback((filter: string) => {
    if (!window.places?.length) {
      console.log('‚ö†Ô∏è –ú–µ—Å—Ç–∞ –µ—â–µ –Ω–µ –∑–∞–≥—Ä—É–∂–µ–Ω—ã');
