import { useEffect, useCallback, useRef } from 'react';
import { useSearchParams } from 'react-router-dom';
import { Place } from '../types/places';

// Глобальная Map для хранения состояния фильтров в UI
if (!window.filterStates) {
  window.filterStates = new Map<string, boolean>();
}

export const useUrlFilters = (
  activeFilters: string[],
  onFilterChange: (filters: string[]) => void,
  filterCounts: Record<string, number>
) => {
  const [searchParams, setSearchParams] = useSearchParams();
  // Используем ref для отслеживания первого рендера
  const isInitialMount = useRef(true);

  // Функция для проверки валидности фильтров
  const getValidFilters = useCallback((currentFilters: string[]) => {
    if (!window.places?.length) return [];

    // Если нет фильтров, то возвращаем пустой массив
    if (!currentFilters.length) return [];

    // Создаем результирующий массив валидных фильтров
    const validFilters: string[] = [];

    // Постепенно фильтруем места, применяя каждый фильтр последовательно
    let filteredPlaces = [...window.places];

    // Проверяем каждый фильтр последовательно
    for (const filter of currentFilters) {
      // Получаем места, соответствующие текущему фильтру из уже отфильтрованных
      const placesWithFilter = filteredPlaces.filter(place => 
        place.attributes.includes(filter)
      );

      // Если есть хотя бы одно место с этим фильтром, считаем его валидным
      if (placesWithFilter.length > 0) {
        validFilters.push(filter);
        filteredPlaces = placesWithFilter; // Обновляем список мест для следующего фильтра
      }
    }

    return validFilters;
  }, []);

  // Эффект для инициализации визуального состояния фильтров
  useEffect(() => {
    // Проверяем, что места загружены и счетчики фильтров рассчитаны
    if (window.places?.length && Object.keys(filterCounts).length > 0) {
      // Сбрасываем состояния фильтров
      window.filterStates.clear();

      // Проходим по всем фильтрам и устанавливаем их состояние
      Object.keys(filterCounts).forEach(filter => {
        if (filterCounts[filter] > 0) {
          window.filterStates.set(filter, true);
        }
      });
    }
  }, [filterCounts, window.places?.length]);

  // Функция для установки фильтров в URL
  const setFiltersInUrl = useCallback((filters: string[] | null) => {
    if (!filters || filters.length === 0) {
      // Удаляем параметр filter из URL
      setSearchParams(new URLSearchParams());
      return;
    }

    // Используем setSearchParams для корректной работы с React Router
    const params = new URLSearchParams();
    params.set('filter', filters.join(','));
    setSearchParams(params);
  }, [setSearchParams]);

  // Обработка изменений URL
  useEffect(() => {
    if (!window.places?.length) return;

    // Получаем фильтры из URL
    const filtersFromUrl = searchParams.get('filter');

    // Если URL пустой, сбрасываем все фильтры
    if (!filtersFromUrl) {
      window.filterStates.clear();
      if (activeFilters.length > 0) {  // Только если есть активные фильтры
        onFilterChange([]);
      }
      return;
    }

    // Получаем список фильтров из URL
    const requestedFilters = filtersFromUrl.split(',').filter(Boolean);

    // Проверяем, все ли фильтры валидны
    const validFilters = getValidFilters(requestedFilters);

    // Если есть невалидные фильтры, обновляем URL
    if (validFilters.join(',') !== requestedFilters.join(',')) {
      if (validFilters.length > 0) {
        setFiltersInUrl(validFilters);
      } else {
        setFiltersInUrl(null);
      }

      // Останавливаем выполнение, т.к. изменение URL вызовет повторный запуск useEffect
      return;
    }

    // Проверяем, изменились ли фильтры
    const currentActiveFiltersStr = activeFilters.sort().join(',');
    const newValidFiltersStr = validFilters.sort().join(',');

    // Обновляем UI-состояние
    window.filterStates.clear();
    validFilters.forEach(filter => {
      window.filterStates.set(filter, true);
    });

    // Вызываем коллбэк только если фильтры изменились
    if (currentActiveFiltersStr !== newValidFiltersStr) {
      onFilterChange(validFilters);
    }
  }, [searchParams, onFilterChange, getValidFilters, setFiltersInUrl, activeFilters]);

  // Обработка клика по фильтру
  const toggleFilter = useCallback((filter: string) => {
    if (!window.places?.length) return;

    // Получаем текущие фильтры из URL
    const urlFilter = searchParams.get('filter') || '';
    const currentFilters = urlFilter ? urlFilter.split(',').filter(Boolean) : [];
    const isActive = currentFilters.includes(filter);

    if (isActive) {
      // Удаляем фильтр из списка
      const newFilters = currentFilters.filter(f => f !== filter);

      // Обновляем UI-состояние
      window.filterStates.delete(filter);

      // Проверяем, изменились ли фильтры
      if (newFilters.join(',') !== currentFilters.join(',')) {
        // Обновляем URL
        if (newFilters.length > 0) {
          setFiltersInUrl(newFilters);
        } else {
          setFiltersInUrl(null);
        }
      }
    } else {
      // Добавляем фильтр, только если у него есть места (счетчик > 0)
      if (filterCounts[filter] && filterCounts[filter] > 0) {
        // Проверяем, будет ли фильтр валидным при добавлении
        const newFilters = [...currentFilters, filter];
        const validFilters = getValidFilters(newFilters);

        // Добавляем фильтр только если он валиден в контексте текущих фильтров
        if (validFilters.includes(filter)) {
          // Обновляем UI-состояние
          window.filterStates.set(filter, true);

          // Проверяем, изменились ли фильтры
          if (validFilters.join(',') !== currentFilters.join(',')) {
            // Обновляем URL
            setFiltersInUrl(validFilters);
          }
        }
      }
    }
  }, [searchParams, filterCounts, getValidFilters, setFiltersInUrl]);

  // Функция для полного сброса фильтров
  const resetFilters = useCallback(() => {
    // Полностью очищаем состояние
    window.filterStates.clear();

    // Очищаем URL
    setFiltersInUrl(null);
  }, [setFiltersInUrl]);

  return {
    toggleFilter,
    isFilterAvailable: useCallback((filter: string) => {
      // Если фильтр уже активен, он доступен
      if (activeFilters.includes(filter)) {
        return true;
      }

      // Иначе проверяем, есть ли для него места
      return (filterCounts[filter] || 0) > 0;
    }, [activeFilters]),
    resetFilters
  };
};

// Добавляем типы для window
declare global {
  interface Window {
    filterStates: Map<string, boolean>;
    places: Place[];
  }
}

export default useUrlFilters;
