import React, { useState, useEffect, useCallback, useMemo, useRef } from 'react';
import { ThemeProvider } from 'styled-components';
import { Header } from './components/Header/Header';
import { Map } from './components/Map/Map';
import { PlacesList } from './components/PlacesList/PlacesList';
import { Filters } from './components/Filters/Filters';
import { Place, AppConfig } from './types/places';
import { useUrlFilters } from './hooks/useUrlFilters';
import { styled } from 'styled-components';
import L from 'leaflet';

const AppContainer = styled.div`
  height: 100vh;
  display: flex;
  flex-direction: column;
`;

const Main = styled.main`
  margin: 56px 0 0;
  min-height: calc(100vh - 56px);
  display: flex;
  flex-direction: column;
  padding: 0;
`;

const Content = styled.div`
  position: relative;
  flex: 1;
  min-height: 0;
  padding: 0;
  display: flex;
`;

const ShowListButton = styled.button`
  position: absolute;
  top: 90px;
  right: 20px;
  padding: 8px 16px;
  background: ${props => props.theme.colors?.primary || '#333'};
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  z-index: 20;
  display: none;

  @media (max-width: 768px) {
    display: block;
  }

  &:hover {
    background: ${props => props.theme.colors?.hover || '#555'};
  }
`;

const NearbyButton = styled.button`
  position: fixed;
  font-size: 18px;
  bottom: 100px;
  left: 50%;
  transform: translateX(-50%);
  padding: 15px 30px;
  background: #FF7F50;
  color: white;
  border: 0;
  border-radius: 50px;
  z-index: 9;
  cursor: pointer;
  font-weight: bold;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
  display: none;
  transition: opacity 0.3s ease, visibility 0.3s ease;

  @media (max-width: 768px) {
    display: block;
  }
`;

const App: React.FC = () => {
  const [config, setConfig] = useState<AppConfig | null>(null);
  const [places, setPlaces] = useState<Place[]>([]);
  const [activeFilters, setActiveFilters] = useState<string[]>([]);
  const [selectedPlace, setSelectedPlace] = useState<Place | null>(null);
  const [isListVisible, setIsListVisible] = useState(window.innerWidth > 768);
  const [isLoading, setIsLoading] = useState(false);
  const mapRef = useRef<L.Map | null>(null);
  const [showNearbyHandler, setShowNearbyHandler] = useState<(() => void) | null>(null);

  // Ð’Ñ‹Ñ‡Ð¸ÑÐ»ÑÐµÐ¼ Ð¾Ñ‚Ñ„Ð¸Ð»ÑŒÑ‚Ñ€Ð¾Ð²Ð°Ð½Ð½Ñ‹Ðµ Ð¼ÐµÑÑ‚Ð° Ñ Ð¿Ð¾Ð¼Ð¾Ñ‰ÑŒÑŽ useMemo
  const filteredPlaces = useMemo(() => {
    if (!places || activeFilters.length === 0) return places;

    // Ð¤Ð¸Ð»ÑŒÑ‚Ñ€ÑƒÐµÐ¼ Ð¼ÐµÑÑ‚Ð°, ÐºÐ¾Ñ‚Ð¾Ñ€Ñ‹Ðµ ÑÐ¾Ð´ÐµÑ€Ð¶Ð°Ñ‚ Ñ…Ð¾Ñ‚Ñ Ð±Ñ‹ Ð¾Ð´Ð¸Ð½ Ð¸Ð· Ð°ÐºÑ‚Ð¸Ð²Ð½Ñ‹Ñ… Ñ„Ð¸Ð»ÑŒÑ‚Ñ€Ð¾Ð²
    return places.filter(place => 
      place.attributes.some(attr => activeFilters.includes(attr))
    );
  }, [places, activeFilters]);

  // Ð’Ñ‹Ñ‡Ð¸ÑÐ»ÑÐµÐ¼ ÐºÐ¾Ð»Ð¸Ñ‡ÐµÑÑ‚Ð²Ð¾ Ð¼ÐµÑÑ‚ Ð´Ð»Ñ ÐºÐ°Ð¶Ð´Ð¾Ð³Ð¾ Ñ„Ð¸Ð»ÑŒÑ‚Ñ€Ð°
  const filterCounts = useMemo(() => {
    const counts: Record<string, number> = {};
    if (!places) return counts;

    places.forEach(place => {
      place.attributes.forEach(attr => {
        counts[attr] = (counts[attr] || 0) + 1;
      });
    });

    return counts;
  }, [places]);

  // Ð—Ð°Ð³Ñ€ÑƒÐ¶Ð°ÐµÐ¼ ÐºÐ¾Ð½Ñ„Ð¸Ð³ÑƒÑ€Ð°Ñ†Ð¸ÑŽ Ð¸ Ð´Ð°Ð½Ð½Ñ‹Ðµ
  useEffect(() => {
    fetch('/data/config.json')
      .then(response => response.json())
      .then(configData => {
        setConfig(configData);

        // Ð—Ð°Ð³Ñ€ÑƒÐ¶Ð°ÐµÐ¼ Ð´Ð°Ð½Ð½Ñ‹Ðµ Ð¾ Ð¼ÐµÑÑ‚Ð°Ñ…
        return fetch('/data/places.json');
      })
      .then(response => response.json())
      .then(placesData => {
        setPlaces(placesData);
        // Ð£ÑÑ‚Ð°Ð½Ð°Ð²Ð»Ð¸Ð²Ð°ÐµÐ¼ Ð¼ÐµÑÑ‚Ð° Ð² Ð³Ð»Ð¾Ð±Ð°Ð»ÑŒÐ½Ð¾Ð¹ Ð¿ÐµÑ€ÐµÐ¼ÐµÐ½Ð½Ð¾Ð¹ Ð´Ð»Ñ Ñ€Ð°Ð±Ð¾Ñ‚Ñ‹ Ñ„Ð¸Ð»ÑŒÑ‚Ñ€Ð¾Ð²
        window.places = placesData;
      })
      .catch(error => console.error('Error loading data:', error));
  }, []);

  // ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚ÐºÐ° Ð¸Ð·Ð¼ÐµÐ½ÐµÐ½Ð¸Ñ Ñ€Ð°Ð·Ð¼ÐµÑ€Ð° Ð¾ÐºÐ½Ð°
  useEffect(() => {
    const handleResize = () => {
      setIsListVisible(window.innerWidth > 768);
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚Ñ‡Ð¸Ðº Ð¸Ð·Ð¼ÐµÐ½ÐµÐ½Ð¸Ñ Ñ„Ð¸Ð»ÑŒÑ‚Ñ€Ð¾Ð²
  const handleFilterChange = useCallback((newFilters: string[]) => {
    console.log('ðŸ“ Ð˜Ð·Ð¼ÐµÐ½ÐµÐ½Ð¸Ðµ Ñ„Ð¸Ð»ÑŒÑ‚Ñ€Ð¾Ð²:', newFilters);
    setActiveFilters(newFilters);
  }, []);

  // ÐŸÐ¾Ð»ÑƒÑ‡Ð°ÐµÐ¼ Ñ„ÑƒÐ½ÐºÑ†Ð¸Ð¸ Ð´Ð»Ñ Ñ€Ð°Ð±Ð¾Ñ‚Ñ‹ Ñ Ñ„Ð¸Ð»ÑŒÑ‚Ñ€Ð°Ð¼Ð¸
  const { toggleFilter, isFilterAvailable, resetFilters } = useUrlFilters(
    activeFilters,
    handleFilterChange,
    filterCounts
  );

  // ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚Ñ‡Ð¸Ðº ÑÐ±Ñ€Ð¾ÑÐ° Ñ„Ð¸Ð»ÑŒÑ‚Ñ€Ð¾Ð²
  const handleReset = useCallback(() => {
    resetFilters();
    setSelectedPlace(null); // Ð¡Ð±Ñ€Ð°ÑÑ‹Ð²Ð°ÐµÐ¼ Ð²Ñ‹Ð±Ñ€Ð°Ð½Ð½Ð¾Ðµ Ð¼ÐµÑÑ‚Ð¾
  }, [resetFilters]);

  const handlePlaceSelect = useCallback((place: Place) => {
    setSelectedPlace(prev => prev?.name === place.name ? null : place);
    if (window.innerWidth <= 768) {
      setIsListVisible(false);
    }
  }, []);

  const handleShowNearby = useCallback(() => {
    // ÐŸÑ€Ð¾Ð²ÐµÑ€ÑÐµÐ¼, Ñ‡Ñ‚Ð¾ Ð¼Ñ‹ Ð½Ð° Ð¼Ð¾Ð±Ð¸Ð»ÑŒÐ½Ð¾Ð¼ ÑƒÑÑ‚Ñ€Ð¾Ð¹ÑÑ‚Ð²Ðµ
    if (window.innerWidth > 768) return;

    if (!showNearbyHandler) return;

    setIsLoading(true);
    showNearbyHandler();
  }, [showNearbyHandler]);

  const handleLoadingComplete = useCallback(() => {
    setIsLoading(false);
  }, []);

  if (!config) return null;

  return (
    <ThemeProvider theme={config.styleSettings}>
      <AppContainer>
        <Header config={config} />
        <Main>
          <Filters
            config={config}
            activeFilters={activeFilters}
            onFilterChange={handleFilterChange}
            filterCounts={filterCounts}
            toggleFilter={toggleFilter}
            isFilterAvailable={isFilterAvailable}
            resetFilters={handleReset}
          />
          <Content>
            <Map
              places={filteredPlaces}
              config={config}
              onMarkerClick={handlePlaceSelect}
              selectedPlace={selectedPlace}
              mapRef={mapRef}
              onShowNearby={setShowNearbyHandler}
              onLoadingComplete={handleLoadingComplete}
            />
            <PlacesList
              places={filteredPlaces}
              config={config}
              onPlaceSelect={handlePlaceSelect}
              selectedPlace={selectedPlace}
              isVisible={isListVisible}
              onClose={() => setIsListVisible(false)}
            />
            <ShowListButton onClick={() => setIsListVisible(true)}>
              {config.content.buttonLabels.showList}
            </ShowListButton>
            <NearbyButton onClick={handleShowNearby}>
              {isLoading ? 'Loading...' : config.content.buttonLabels.showNearby}
            </NearbyButton>
          </Content>
        </Main>
      </AppContainer>
    </ThemeProvider>
  );
};

export default App;
