import React, { useState, useEffect, useCallback, useMemo, useRef } from 'react';
import { ThemeProvider } from 'styled-components';
import { Header } from './components/Header/Header';
import { Map } from './components/Map/Map';
import { PlacesList } from './components/PlacesList/PlacesList';
import { Filters } from './components/Filters/Filters';
import { Place, AppConfig } from './types/places';
import { useUrlFilters } from './hooks/useUrlFilters';
import { styled } from 'styled-components';
import L from 'leaflet';

const AppContainer = styled.div`
  height: 100vh;
  display: flex;
  flex-direction: column;
`;

const Main = styled.main`
  margin: 56px 0 0;
  min-height: calc(100vh - 56px);
  display: flex;
  flex-direction: column;
  padding: 0;
`;

const Content = styled.div`
  position: relative;
  flex: 1;
  min-height: 0;
  padding: 0;
  display: flex;
`;

const ShowListButton = styled.button`
  position: absolute;
  top: 90px;
  right: 20px;
  padding: 8px 16px;
  background: ${props => props.theme.colors?.primary || '#333'};
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  z-index: 20;
  display: none;

  @media (max-width: 768px) {
    display: block;
  }

  &:hover {
    background: ${props => props.theme.colors?.hover || '#555'};
  }
`;

const NearbyButton = styled.button`
  position: fixed;
  font-size: 18px;
  bottom: 100px;
  left: 50%;
  transform: translateX(-50%);
  padding: 15px 30px;
  background: #FF7F50;
  color: white;
  border: 0;
  border-radius: 50px;
  z-index: 9;
  cursor: pointer;
  font-weight: bold;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
  display: none;
  transition: opacity 0.3s ease, visibility 0.3s ease;

  @media (max-width: 768px) {
    display: block;
  }
`;

const App: React.FC = () => {
  const [config, setConfig] = useState<AppConfig | null>(null);
  const [places, setPlaces] = useState<Place[]>([]);
  const [activeFilters, setActiveFilters] = useState<string[]>([]);
  const [selectedPlace, setSelectedPlace] = useState<Place | null>(null);
  const [isListVisible, setIsListVisible] = useState(window.innerWidth > 768);
  const [isLoading, setIsLoading] = useState(false);
  const mapRef = useRef<L.Map | null>(null);
  const userMarkerRef = useRef<L.Marker | null>(null);
  const [mapCenter, setMapCenter] = useState<[number, number]>([0, 0]);

  // –í—ã—á–∏—Å–ª—è–µ–º –æ—Ç—Ñ–∏–ª—å—Ç—Ä–æ–≤–∞–Ω–Ω—ã–µ –º–µ—Å—Ç–∞ —Å –ø–æ–º–æ—â—å—é useMemo
  const filteredPlaces = useMemo(() => {
    if (!places || activeFilters.length === 0) return places;

    // –§–∏–ª—å—Ç—Ä—É–µ–º –º–µ—Å—Ç–∞, –∫–æ—Ç–æ—Ä—ã–µ —Å–æ–¥–µ—Ä–∂–∞—Ç —Ö–æ—Ç—è –±—ã –æ–¥–∏–Ω –∏–∑ –∞–∫—Ç–∏–≤–Ω—ã—Ö —Ñ–∏–ª—å—Ç—Ä–æ–≤
    return places.filter(place => 
      place.attributes.some(attr => activeFilters.includes(attr))
    );
  }, [places, activeFilters]);

  // –°–æ—Ä—Ç–∏—Ä—É–µ–º –º–µ—Å—Ç–∞ –ø–æ —Ä–∞—Å—Å—Ç–æ—è–Ω–∏—é –æ—Ç —Ü–µ–Ω—Ç—Ä–∞ –∫–∞—Ä—Ç—ã
  const sortedPlaces = useMemo(() => {
    if (!filteredPlaces) return [];

    // –í—ã—á–∏—Å–ª—è–µ–º —Ä–∞—Å—Å—Ç–æ—è–Ω–∏–µ –æ—Ç —Ü–µ–Ω—Ç—Ä–∞ –¥–ª—è –∫–∞–∂–¥–æ–≥–æ –º–µ—Å—Ç–∞
    const getDistance = (lat1: number, lng1: number, lat2: number, lng2: number) => {
      // –ò—Å–ø–æ–ª—å–∑—É–µ–º —Ñ–æ—Ä–º—É–ª—É –≥–∞–≤–µ—Ä—Å–∏–Ω—É—Å–∞ –¥–ª—è –≤—ã—á–∏—Å–ª–µ–Ω–∏—è —Ä–∞—Å—Å—Ç–æ—è–Ω–∏—è –º–µ–∂–¥—É –¥–≤—É–º—è —Ç–æ—á–∫–∞–º–∏ –Ω–∞ —Å—Ñ–µ—Ä–µ
      const R = 6371; // —Ä–∞–¥–∏—É—Å –ó–µ–º–ª–∏ –≤ –∫–º
      const dLat = (lat2 - lat1) * Math.PI / 180;
      const dLng = (lng2 - lng1) * Math.PI / 180;
      const a = 
        Math.sin(dLat/2) * Math.sin(dLat/2) +
        Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) * 
        Math.sin(dLng/2) * Math.sin(dLng/2);
      const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a)); 
      const distance = R * c; // —Ä–∞—Å—Å—Ç–æ—è–Ω–∏–µ –≤ –∫–º
      return distance;
    };

    // –ö–æ–ø–∏—Ä—É–µ–º –º–∞—Å—Å–∏–≤ –∏ —Å–æ—Ä—Ç–∏—Ä—É–µ–º –µ–≥–æ
    return [...filteredPlaces].sort((a, b) => {
      const distA = getDistance(a.lat, a.lng, mapCenter[0], mapCenter[1]);
      const distB = getDistance(b.lat, b.lng, mapCenter[0], mapCenter[1]);
      return distA - distB;
    });
  }, [filteredPlaces, mapCenter]);

  // –í—ã—á–∏—Å–ª—è–µ–º –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –º–µ—Å—Ç –¥–ª—è –∫–∞–∂–¥–æ–≥–æ —Ñ–∏–ª—å—Ç—Ä–∞
  const filterCounts = useMemo(() => {
    const counts: Record<string, number> = {};
    if (!places) return counts;

    places.forEach(place => {
      place.attributes.forEach(attr => {
        counts[attr] = (counts[attr] || 0) + 1;
      });
    });

    return counts;
  }, [places]);

  // –ó–∞–≥—Ä—É–∂–∞–µ–º –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏—é –∏ –¥–∞–Ω–Ω—ã–µ
  useEffect(() => {
    fetch('/data/config.json')
      .then(response => response.json())
      .then(configData => {
        setConfig(configData);

        // –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –Ω–∞—á–∞–ª—å–Ω—ã–π —Ü–µ–Ω—Ç—Ä –∫–∞—Ä—Ç—ã –∏–∑ –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–∏
        if (configData.mapSettings && configData.mapSettings.center) {
          setMapCenter(configData.mapSettings.center);
        }

        // –ó–∞–≥—Ä—É–∂–∞–µ–º –¥–∞–Ω–Ω—ã–µ –æ –º–µ—Å—Ç–∞—Ö
        return fetch('/data/places.json');
      })
      .then(response => response.json())
      .then(placesData => {
        setPlaces(placesData);
        // –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –º–µ—Å—Ç–∞ –≤ –≥–ª–æ–±–∞–ª—å–Ω–æ–π –ø–µ—Ä–µ–º–µ–Ω–Ω–æ–π –¥–ª—è —Ä–∞–±–æ—Ç—ã —Ñ–∏–ª—å—Ç—Ä–æ–≤
        window.places = placesData;
      })
      .catch(error => console.error('Error loading data:', error));
  }, []);

  // –û–±—Ä–∞–±–æ—Ç–∫–∞ –∏–∑–º–µ–Ω–µ–Ω–∏—è —Ä–∞–∑–º–µ—Ä–∞ –æ–∫–Ω–∞
  useEffect(() => {
    const handleResize = () => {
      setIsListVisible(window.innerWidth > 768);
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∏–∑–º–µ–Ω–µ–Ω–∏—è —Ñ–∏–ª—å—Ç—Ä–æ–≤
  const handleFilterChange = useCallback((newFilters: string[]) => {
    console.log('üìç –ò–∑–º–µ–Ω–µ–Ω–∏–µ —Ñ–∏–ª—å—Ç—Ä–æ–≤:', newFilters);
    setActiveFilters(newFilters);
  }, []);

  // –ü–æ–ª—É—á–∞–µ–º —Ñ—É–Ω–∫—Ü–∏–∏ –¥–ª—è —Ä–∞–±–æ—Ç—ã —Å —Ñ–∏–ª—å—Ç—Ä–∞–º–∏
  const { toggleFilter, isFilterAvailable, resetFilters } = useUrlFilters(
    activeFilters,
    handleFilterChange,
    filterCounts
  );

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ —Å–±—Ä–æ—Å–∞ —Ñ–∏–ª—å—Ç—Ä–æ–≤
  const handleReset = useCallback(() => {
    resetFilters();
    setSelectedPlace(null); // –°–±—Ä–∞—Å—ã–≤–∞–µ–º –≤—ã–±—Ä–∞–Ω–Ω–æ–µ –º–µ—Å—Ç–æ
  }, [resetFilters]);

  const handlePlaceSelect = useCallback((place: Place) => {
    // –£–¥–∞–ª—è–µ–º –º–∞—Ä–∫–µ—Ä –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –ø—Ä–∏ –≤—ã–±–æ—Ä–µ –Ω–æ–≤–æ–≥–æ –º–µ—Å—Ç–∞
    if (userMarkerRef.current && mapRef.current) {
      userMarkerRef.current.remove();
      userMarkerRef.current = null;
    }

    // –ï—Å–ª–∏ –≤—ã–±—Ä–∞–ª–∏ —Ç–æ –∂–µ –º–µ—Å—Ç–æ, –∫–æ—Ç–æ—Ä–æ–µ —É–∂–µ –≤—ã–±—Ä–∞–Ω–æ, —Ç–æ —Å–±—Ä–∞—Å—ã–≤–∞–µ–º –≤—ã–±–æ—Ä
    if (selectedPlace && selectedPlace.name === place.name) {
      setSelectedPlace(null);
    } else {
      // –ò–Ω–∞—á–µ —É—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –Ω–æ–≤–æ–µ –≤—ã–±—Ä–∞–Ω–Ω–æ–µ –º–µ—Å—Ç–æ
      setSelectedPlace(place);
    }

    // –°–∫—Ä—ã–≤–∞–µ–º —Å–ø–∏—Å–æ–∫ –º–µ—Å—Ç –Ω–∞ –º–æ–±–∏–ª—å–Ω—ã—Ö —É—Å—Ç—Ä–æ–π—Å—Ç–≤–∞—Ö
    if (window.innerWidth <= 768) {
      setIsListVisible(false);
    }
  }, [mapRef, selectedPlace]);

  const handleShowNearby = useCallback(() => {
    // –ì–µ–æ–ª–æ–∫–∞—Ü–∏—è —Ç–æ–ª—å–∫–æ –¥–ª—è –º–æ–±–∏–ª—å–Ω—ã—Ö —É—Å—Ç—Ä–æ–π—Å—Ç–≤
    if (window.innerWidth > 768) return;

    // –ù–∞—á–∏–Ω–∞–µ–º –∑–∞–≥—Ä—É–∑–∫—É
    setIsLoading(true);

    // –ó–∞–ø—Ä–∞—à–∏–≤–∞–µ–º –≥–µ–æ–ª–æ–∫–∞—Ü–∏—é
    if (!navigator.geolocation) {
      alert('Geolocation is not supported by your browser');
      setIsLoading(false);
      return;
    }

    navigator.geolocation.getCurrentPosition(
      position => {
        const userLat = position.coords.latitude;
        const userLng = position.coords.longitude;

        // –¶–µ–Ω—Ç—Ä–∏—Ä—É–µ–º –∫–∞—Ä—Ç—É –ø–æ –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç–∞–º –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
        if (mapRef.current) {
          // –°–±—Ä–∞—Å—ã–≤–∞–µ–º –≤—ã–±—Ä–∞–Ω–Ω–æ–µ –º–µ—Å—Ç–æ –ø—Ä–∏ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏–∏ –≥–µ–æ–ª–æ–∫–∞—Ü–∏–∏
          setSelectedPlace(null);

          mapRef.current.setView([userLat, userLng], 16, {
            animate: true,
            duration: 0.5
          });

          // –£–¥–∞–ª—è–µ–º –ø—Ä–µ–¥—ã–¥—É—â–∏–π –º–∞—Ä–∫–µ—Ä –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
          if (userMarkerRef.current) {
            userMarkerRef.current.remove();
            userMarkerRef.current = null;
          }

          // –°–æ–∑–¥–∞–µ–º –Ω–æ–≤—ã–π –º–∞—Ä–∫–µ—Ä –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
          const userMarker = L.marker([userLat, userLng], {
            icon: L.icon({
              iconUrl: '/data/images/pin.svg',
              iconSize: [30, 40],
              iconAnchor: [15, 40]
            })
          }).addTo(mapRef.current);

          // –°–æ—Ö—Ä–∞–Ω—è–µ–º —Å—Å—ã–ª–∫—É –Ω–∞ –º–∞—Ä–∫–µ—Ä –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
          userMarkerRef.current = userMarker;
        }

        // –ó–∞–≤–µ—Ä—à–∞–µ–º –∑–∞–≥—Ä—É–∑–∫—É
        setIsLoading(false);
      },
      error => {
        console.error('Error getting location:', error);
        alert('Unable to get your location');
        setIsLoading(false);
      }
    );
  }, [mapRef]);

  const handlePopupClose = useCallback(() => {
    // –ü—Ä–∏ –∑–∞–∫—Ä—ã—Ç–∏–∏ –ø–æ–ø–∞–ø–∞ —á–µ—Ä–µ–∑ –∫—Ä–µ—Å—Ç–∏–∫ —Å–±—Ä–∞—Å—ã–≤–∞–µ–º –≤—ã–±—Ä–∞–Ω–Ω–æ–µ –º–µ—Å—Ç–æ
    setSelectedPlace(null);

    // –¢–∞–∫–∂–µ —É–¥–∞–ª—è–µ–º –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å—Å–∫–∏–π –º–∞—Ä–∫–µ—Ä, –µ—Å–ª–∏ –æ–Ω —Å—É—â–µ—Å—Ç–≤—É–µ—Ç
    if (userMarkerRef.current && mapRef.current) {
      userMarkerRef.current.remove();
      userMarkerRef.current = null; 
    }
  }, [mapRef]);

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∏–∑–º–µ–Ω–µ–Ω–∏—è —Ü–µ–Ω—Ç—Ä–∞ –∫–∞—Ä—Ç—ã
  const handleMapMoveEnd = useCallback((center: [number, number]) => {
    setMapCenter(center);
  }, []);

  if (!config) return null;

  return (
    <ThemeProvider theme={config.styleSettings}>
      <AppContainer>
        <Header config={config} />
        <Main>
          <Filters
            config={config}
            activeFilters={activeFilters}
            onFilterChange={handleFilterChange}
            filterCounts={filterCounts}
            toggleFilter={toggleFilter}
            isFilterAvailable={isFilterAvailable}
            resetFilters={handleReset}
          />
          <Content>
            <Map
              places={sortedPlaces}
              config={config}
              onMarkerClick={handlePlaceSelect}
              selectedPlace={selectedPlace}
              mapRef={mapRef}
              onPopupClose={handlePopupClose}
              onMapMoveEnd={handleMapMoveEnd}
            />
            <PlacesList
              places={sortedPlaces}
              config={config}
              onPlaceSelect={handlePlaceSelect}
              selectedPlace={selectedPlace}
              isVisible={isListVisible}
              onClose={() => setIsListVisible(false)}
            />
            <ShowListButton onClick={() => setIsListVisible(true)}>
              {config.content.buttonLabels.showList}
            </ShowListButton>
            <NearbyButton onClick={handleShowNearby}>
              {isLoading ? 'Loading...' : config.content.buttonLabels.showNearby}
            </NearbyButton>
          </Content>
        </Main>
      </AppContainer>
    </ThemeProvider>
  );
};

export default App;
