      console.log('‚ûï –ü—Ä–æ–≤–µ—Ä—è–µ–º –≤–æ–∑–º–æ–∂–Ω–æ—Å—Ç—å –¥–æ–±–∞–≤–ª–µ–Ω–∏—è —Ñ–∏–ª—å—Ç—Ä–∞');

      // –ü–æ–ª—É—á–∞–µ–º –±–∞–∑–æ–≤—ã–µ —Å—á–µ—Ç—á–∏–∫–∏
      const baseCounts = calculateBaseCounts(window.places);

