    // –ü–æ–ª—É—á–∞–µ–º –±–∞–∑–æ–≤—ã–µ —Å—á–µ—Ç—á–∏–∫–∏
    const baseCounts = calculateBaseCounts(window.places);

    // –ü–æ–ª—É—á–∞–µ–º –º–µ—Å—Ç–∞, –æ—Ç—Ñ–∏–ª—å—Ç—Ä–æ–≤–∞–Ω–Ω—ã–µ –ø–æ —Ç–µ–∫—É—â–∏–º –∞–∫—Ç–∏–≤–Ω—ã–º —Ñ–∏–ª—å—Ç—Ä–∞–º
    const { filteredPlaces } = applyFiltersSequentially(
      window.places,
      currentFilters,
      baseCounts
    );

    // –°—á–∏—Ç–∞–µ–º –º–µ—Å—Ç–∞ —Å –Ω–æ–≤—ã–º —Ñ–∏–ª—å—Ç—Ä–æ–º
    const placesWithNewFilter = filteredPlaces.filter(
      place => place.attributes.includes(filter)
