import React, { useState, useEffect, useCallback, useMemo, useRef } from 'react';
import { ThemeProvider } from 'styled-components';
import { Header } from './components/Header/Header';
import { Map } from './components/Map/Map';
import { PlacesList } from './components/PlacesList/PlacesList';
import { Filters } from './components/Filters/Filters';
import { Place, AppConfig } from './types/places';
import { useUrlFilters } from './hooks/useUrlFilters';
import { styled } from 'styled-components';
import L from 'leaflet';

const AppContainer = styled.div`
  height: 100vh;
  display: flex;
  flex-direction: column;
`;

const Main = styled.main`
  margin: 56px 0 0;
  min-height: calc(100vh - 56px);
  display: flex;
  flex-direction: column;
  padding: 0;
`;

const Content = styled.div`
  position: relative;
  flex: 1;
  min-height: 0;
  padding: 0;
  display: flex;
`;

const ShowListButton = styled.button`
  position: absolute;
  top: 90px;
  right: 20px;
  padding: 8px 16px;
  background: ${props => props.theme.colors?.primary || '#333'};
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  z-index: 20;
  display: none;

  @media (max-width: 768px) {
    display: block;
  }

  &:hover {
    background: ${props => props.theme.colors?.hover || '#555'};
  }
`;

const NearbyButton = styled.button`
  position: fixed;
  font-size: 18px;
  bottom: 100px;
  left: 50%;
  transform: translateX(-50%);
  padding: 15px 30px;
  background: #FF7F50;
  color: white;
  border: 0;
  border-radius: 50px;
  z-index: 9;
  cursor: pointer;
  font-weight: bold;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
  display: none;
  transition: opacity 0.3s ease, visibility 0.3s ease;

  @media (max-width: 768px) {
    display: block;
  }
`;

const App: React.FC = () => {
  const [config, setConfig] = useState<AppConfig | null>(null);
  const [places, setPlaces] = useState<Place[]>([]);
  const [activeFilters, setActiveFilters] = useState<string[]>([]);
  const [selectedPlace, setSelectedPlace] = useState<Place | null>(null);
  const [isListVisible, setIsListVisible] = useState(window.innerWidth > 768);
  const [isLoading, setIsLoading] = useState(false);
  const mapRef = useRef<L.Map | null>(null);
  const userMarkerRef = useRef<L.Marker | null>(null);

  // Вычисляем отфильтрованные места с помощью useMemo
  const filteredPlaces = useMemo(() => {
    if (!places || activeFilters.length === 0) return places;

    // Фильтруем места, которые содержат хотя бы один из активных фильтров
    return places.filter(place => 
      place.attributes.some(attr => activeFilters.includes(attr))
    );
  }, [places, activeFilters]);

  // Вычисляем количество мест для каждого фильтра
  const filterCounts = useMemo(() => {
    const counts: Record<string, number> = {};
    if (!places) return counts;

    places.forEach(place => {
      place.attributes.forEach(attr => {
        counts[attr] = (counts[attr] || 0) + 1;
      });
    });

    return counts;
  }, [places]);

  // Загружаем конфигурацию и данные
  useEffect(() => {
    fetch('/data/config.json')
      .then(response => response.json())
      .then(configData => {
        setConfig(configData);

        // Загружаем данные о местах
        return fetch('/data/places.json');
      })
      .then(response => response.json())
      .then(placesData => {
        setPlaces(placesData);
        // Устанавливаем места в глобальной переменной для работы фильтров
        window.places = placesData;
      })
      .catch(error => console.error('Error loading data:', error));
  }, []);

  // Обработка изменения размера окна
  useEffect(() => {
    const handleResize = () => {
      setIsListVisible(window.innerWidth > 768);
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Обработчик изменения фильтров
  const handleFilterChange = useCallback((newFilters: string[]) => {
    console.log('📍 Изменение фильтров:', newFilters);
    setActiveFilters(newFilters);
  }, []);

  // Получаем функции для работы с фильтрами
  const { toggleFilter, isFilterAvailable, resetFilters } = useUrlFilters(
    activeFilters,
    handleFilterChange,
    filterCounts
  );

  // Обработчик сброса фильтров
  const handleReset = useCallback(() => {
    resetFilters();
    setSelectedPlace(null); // Сбрасываем выбранное место
  }, [resetFilters]);

  const handlePlaceSelect = useCallback((place: Place) => {
    // Перед выбором нового места, удаляем маркер пользователя, если он существует
    if (userMarkerRef.current && mapRef.current) {
      userMarkerRef.current.remove();
      userMarkerRef.current = null;
    }

    setSelectedPlace(prev => prev?.name === place.name ? null : place);
    if (window.innerWidth <= 768) {
      setIsListVisible(false);
    }
  }, [mapRef]);

  const handleShowNearby = useCallback(() => {
    // Только для мобильных устройств
    if (window.innerWidth > 768) return;

    // Начинаем загрузку
    setIsLoading(true);

    // Запрашиваем геолокацию
    if (!navigator.geolocation) {
      alert('Geolocation is not supported by your browser');
      setIsLoading(false);
      return;
    }

    navigator.geolocation.getCurrentPosition(
      position => {
        const userLat = position.coords.latitude;
        const userLng = position.coords.longitude;

        // Центрируем карту по координатам пользователя
        if (mapRef.current) {
          mapRef.current.setView([userLat, userLng], 16, {
            animate: true,
            duration: 0.5
          });

          // Удаляем предыдущий маркер пользователя, если он существует
          if (userMarkerRef.current) {
            userMarkerRef.current.remove();
          }

          // Устанавливаем маркер с кастомной иконкой
          const userMarker = L.marker([userLat, userLng], {
            icon: L.icon({
              iconUrl: '/data/images/pin.svg',
              iconSize: [30, 40],
              iconAnchor: [15, 40]
            })
          }).addTo(mapRef.current);

          // Сохраняем ссылку на маркер пользователя
          userMarkerRef.current = userMarker;
        }

        // Завершаем загрузку
        setIsLoading(false);
      },
      error => {
        console.error('Error getting location:', error);
        alert('Unable to get your location');
        setIsLoading(false);
      }
    );
  }, [mapRef]);

  const handlePopupClose = useCallback(() => {
    setSelectedPlace(null);
  }, []);

  if (!config) return null;

  return (
    <ThemeProvider theme={config.styleSettings}>
      <AppContainer>
        <Header config={config} />
        <Main>
          <Filters
            config={config}
            activeFilters={activeFilters}
            onFilterChange={handleFilterChange}
            filterCounts={filterCounts}
            toggleFilter={toggleFilter}
            isFilterAvailable={isFilterAvailable}
            resetFilters={handleReset}
          />
          <Content>
            <Map
              places={filteredPlaces}
              config={config}
              onMarkerClick={handlePlaceSelect}
              selectedPlace={selectedPlace}
              mapRef={mapRef}
              onPopupClose={handlePopupClose}
            />
            <PlacesList
              places={filteredPlaces}
              config={config}
              onPlaceSelect={handlePlaceSelect}
              selectedPlace={selectedPlace}
              isVisible={isListVisible}
              onClose={() => setIsListVisible(false)}
            />
            <ShowListButton onClick={() => setIsListVisible(true)}>
              {config.content.buttonLabels.showList}
            </ShowListButton>
            <NearbyButton onClick={handleShowNearby}>
              {isLoading ? 'Loading...' : config.content.buttonLabels.showNearby}
            </NearbyButton>
          </Content>
        </Main>
      </AppContainer>
    </ThemeProvider>
  );
};

export default App;
