import React, { useState, useCallback } from 'react';
import { styled } from 'styled-components';
import { Filter, AppConfig } from '../../types/places';

interface FiltersProps {
  config: AppConfig;
  activeFilters: string[];
  onFilterChange: (filters: string[]) => void;
  filterCounts: Record<string, number>;
  toggleFilter: (filter: string) => void;
  isFilterAvailable: (filter: string) => boolean;
  resetFilters: () => void;
}

const FiltersBlock = styled.div`
  margin: 0;
  padding: 5px;
  flex-shrink: 0;
  min-height: 0;
  display: block !important;
  position: sticky;
  top: 56px;
  z-index: 50;
  background: #fff;
  contain: layout paint;
  will-change: transform;
  transform: translateZ(0);
  -webkit-font-smoothing: antialiased;
`;

const FiltersContainer = styled.div`
  display: flex;
  justify-content: flex-start;
  max-width: 1200px;
  margin: 0 auto;
  gap: 0;
  flex-wrap: wrap;
  align-items: flex-start;
  padding: 0;
  min-height: 0;
  contain: layout style paint;

  @media (max-width: 768px) {
    display: none !important;
  }
`;

const FilterGroup = styled.div<{ $isRight?: boolean }>`
  display: flex;
  flex-wrap: wrap;
  gap: 3px;
  padding: 0 5px;
  margin: 0;
  contain: layout style;
  position: relative;
  max-width: ${props => props.$isRight ? '790px' : '290px'};

  ${props => !props.$isRight && `
    &::after {
      content: '';
      position: absolute;
      right: 0;
      top: 0;
      height: 100%;
      width: 1px;
      background: #ccc;
      transform: translateZ(0);
    }
  `}
`;

const FilterButton = styled.div<{ $isActive: boolean; $isDisabled: boolean }>`
  padding: 2px 4px;
  border: 1px solid #ccc;
  border-radius: 20px;
  cursor: ${props => props.$isDisabled ? 'default' : 'pointer'};
  user-select: none;
  background: ${props => props.$isActive ? props.theme.colors?.primary || '#333' : '#fff'};
  color: ${props => props.$isActive ? '#fff' : props.theme.colors?.primary || '#333'};
  text-align: center;
  white-space: nowrap;
  font-size: 11px;
  box-sizing: border-box;
  display: flex;
  align-items: center;
  justify-content: space-between;
  position: relative;
  transition: all 0.2s ease-out;
  will-change: transform, opacity;
  transform: translateZ(0);
  backface-visibility: hidden;
  opacity: ${props => props.$isDisabled ? 0.5 : 1};

  &:hover {
    background: ${props => !props.$isDisabled && (props.$isActive ? props.theme.colors?.primary || '#333' : '#f5f5f5')};
  }
`;

const FilterIcon = styled.img`
  width: 16px;
  height: 16px;
  margin-right: 5px;
  vertical-align: middle;
  opacity: 0;
  transition: opacity 0.3s ease-in-out;
  transform: translateZ(0);
  content-visibility: auto;

  &[src] {
    opacity: 1;
  }
`;

const FilterCount = styled.span<{ $isActive: boolean }>`
  margin-left: 5px;
  font-size: 10px;
  color: ${props => props.$isActive ? '#fff' : props.theme.colors?.textSecondary || '#666'};
  background: ${props => props.$isActive ? props.theme.colors?.hover || '#555' : '#f0f0f0'};
  border-radius: 50%;
  width: 20px;
  height: 20px;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: all 0.2s ease-out;
  transform: ${props => props.$isActive ? 'scale(1.05)' : 'scale(1)'} translateZ(0);
`;

const ResetButton = styled.button`
  font-size: 14px;
  cursor: pointer;
  padding: 5px 10px;
  border-radius: 9px;
  background-color: #FFEFEF;
  font-weight: bold;
  border: none;
  align-items: center;
  transition: all 0.2s ease-out;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-left: 10px;
  will-change: transform, background-color;
  transform: translateZ(0);

  &:hover {
    background-color: #ffbbbb;
    transform: translateY(-1px) translateZ(0);
  }
`;

const MobileFilters = styled.div`
  display: none;

  @media (max-width: 768px) {
    display: flex !important;
    gap: 3px;
    align-items: flex-start;
    width: 100%;
    flex-wrap: wrap;
    justify-content: flex-start;
    position: relative;
    z-index: 50;
    contain: layout style;
    transform: translateZ(0);
    will-change: transform;
  }
`;

export const Filters: React.FC<FiltersProps> = ({
  config,
  activeFilters,
  onFilterChange,
  filterCounts,
  toggleFilter,
  isFilterAvailable,
  resetFilters
}) => {
  const handleReset = useCallback(() => {
    resetFilters();
  }, [resetFilters]);

  const renderFilterButton = (filter: Filter) => (
    <FilterButton
      key={filter.key}
      $isActive={activeFilters.includes(filter.key)}
      $isDisabled={!isFilterAvailable(filter.key)}
      onClick={() => toggleFilter(filter.key)}
      title={filter.tooltip}
    >
      <FilterIcon src={filter.icon} alt={filter.label} />
      {filter.label}
      <FilterCount $isActive={activeFilters.includes(filter.key)}>
        {filterCounts[filter.key] || 0}
      </FilterCount>
    </FilterButton>
  );

  return (
    <FiltersBlock>
      <FiltersContainer>
        <FilterGroup>
          {config.filters.leftFilters.map(renderFilterButton)}
        </FilterGroup>
        <FilterGroup $isRight>
          {config.filters.rightFilters.map(renderFilterButton)}
        </FilterGroup>
        <ResetButton onClick={handleReset}>
          <img src="/data/images/reset.svg" alt="Reset" style={{ width: '16px', height: '16px', marginRight: '5px' }} />
        </ResetButton>
      </FiltersContainer>

      <MobileFilters>
        <FilterGroup>
          {config.filters.leftFilters.slice(0, 2).map(renderFilterButton)}
        </FilterGroup>
        <ResetButton onClick={handleReset}>
          <img src="/data/images/reset.svg" alt="Reset" style={{ width: '16px', height: '16px', marginRight: '5px' }} />
        </ResetButton>
        <FilterGroup $isRight>
          {config.filters.rightFilters.slice(0, 3).map(renderFilterButton)}
        </FilterGroup>
      </MobileFilters>
    </FiltersBlock>
  );
};

export default Filters;
