import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { ThemeProvider } from 'styled-components';
import { Header } from './components/Header/Header';
import { Map } from './components/Map/Map';
import { PlacesList } from './components/PlacesList/PlacesList';
import { Filters } from './components/Filters/Filters';
import { Place, AppConfig } from './types/places';
import { useUrlFilters } from './hooks/useUrlFilters';
import { styled } from 'styled-components';
import L from 'leaflet';

const AppContainer = styled.div`
  height: 100vh;
  display: flex;
  flex-direction: column;
`;

const Main = styled.main`
  margin: 56px 0 0;
  min-height: calc(100vh - 56px);
  display: flex;
  flex-direction: column;
  padding: 0;
`;

const Content = styled.div`
  position: relative;
  flex: 1;
  min-height: 0;
  padding: 0;
  display: flex;
`;

const ShowListButton = styled.button`
  position: absolute;
  top: 90px;
  right: 20px;
  padding: 8px 16px;
  background: ${props => props.theme.colors?.primary || '#333'};
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  z-index: 20;
  display: none;

  @media (max-width: 768px) {
    display: block;
  }

  &:hover {
    background: ${props => props.theme.colors?.hover || '#555'};
  }
`;

const NearbyButton = styled.button`
  position: fixed;
  font-size: 18px;
  bottom: 100px;
  left: 50%;
  transform: translateX(-50%);
  padding: 15px 30px;
  background: #FF7F50;
  color: white;
  border: 0;
  border-radius: 50px;
  z-index: 9;
  cursor: pointer;
  font-weight: bold;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
  display: none;
  transition: opacity 0.3s ease, visibility 0.3s ease;

  @media (max-width: 768px) {
    display: block;
  }
`;

const App: React.FC = () => {
  const [config, setConfig] = useState<AppConfig | null>(null);
  const [places, setPlaces] = useState<Place[]>([]);
  const [activeFilters, setActiveFilters] = useState<string[]>([]);
  const [selectedPlace, setSelectedPlace] = useState<Place | null>(null);
  const [isListVisible, setIsListVisible] = useState(window.innerWidth > 768);
  const [isLoading, setIsLoading] = useState(false);
  const mapRef = React.useRef<L.Map | null>(null);
  const activeMarkerRef = React.useRef<L.Marker | null>(null);

  // Вычисляем отфильтрованные места с помощью useMemo
  const filteredPlaces = useMemo(() => {
    console.log('📊 Вычисляем отфильтрованные места');
    console.log('📊 Активные фильтры:', activeFilters);

    if (activeFilters.length === 0) {
      console.log('📊 Нет активных фильтров, возвращаем все места:', places.length);
      return places;
    }

    const filtered = places.filter(place =>
      activeFilters.every(filter => place.attributes.includes(filter))
    );
    console.log('📊 Отфильтрованные места:', filtered.length);
    return filtered;
  }, [places, activeFilters]);

  // Вычисляем количество мест для каждого фильтра с помощью useMemo
  const filterCounts = useMemo(() => {
    console.log('\n🔢 Начинаем подсчет фильтров');
    console.log('🔢 Активные фильтры:', activeFilters);

    const counts: Record<string, number> = {};
    const activeFilterSet = new Set(activeFilters);

    console.log('🔢 Проходим по всем местам:', places.length);
    places.forEach(place => {
      console.log(`\n🏠 Обрабатываем место: ${place.name}`);
      console.log('🏠 Атрибуты места:', place.attributes);

      place.attributes.forEach(attr => {
        if (!counts[attr]) counts[attr] = 0;

        // Проверяем, подходит ли место под другие активные фильтры
        const otherFilters = Array.from(activeFilterSet).filter(f => f !== attr);
        console.log(`  🔍 Проверяем атрибут "${attr}"`);
        console.log(`  🔍 Другие активные фильтры:`, otherFilters);

        if (otherFilters.length === 0) {
          counts[attr]++;
          console.log(`  ✅ Нет других фильтров, увеличиваем счетчик для ${attr}: ${counts[attr]}`);
        } else if (otherFilters.every(f => place.attributes.includes(f))) {
          counts[attr]++;
          console.log(`  ✅ Место подходит под все фильтры, увеличиваем счетчик для ${attr}: ${counts[attr]}`);
        } else {
          console.log(`  ❌ Место не подходит под другие фильтры для ${attr}, счетчик: ${counts[attr]}`);
        }
      });
    });

    console.log('\n📈 Итоговые счетчики фильтров:', counts);
    return counts;
  }, [places, activeFilters]);

  useEffect(() => {
    // Загрузка конфигурации
    fetch('/data/config.json')
      .then(response => response.json())
      .then(data => setConfig(data))
      .catch(error => console.error('Error loading config:', error));

    // Загрузка мест
    fetch('/data/places.json')
      .then(response => response.json())
      .then(data => {
        setPlaces(data);
        // Делаем места доступными глобально для useUrlFilters
        window.places = data;
      })
      .catch(error => console.error('Error loading places:', error));

    // Устанавливаем начальную видимость списка мест
    const handleResize = () => {
      setIsListVisible(window.innerWidth > 768);
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // Обработчик изменения фильтров
  const handleFilterChange = useCallback((newFilters: string[]) => {
    console.log('📍 Изменение фильтров:', newFilters);
    setActiveFilters(newFilters);
  }, []);

  // Получаем функции для работы с фильтрами
  const { toggleFilter, isFilterAvailable, resetFilters } = useUrlFilters(
    activeFilters,
    handleFilterChange,
    filterCounts
  );

  // Обработчик сброса фильтров
  const handleReset = useCallback(() => {
    resetFilters();
    setSelectedPlace(null); // Сбрасываем выбранное место
  }, [resetFilters]);

  const handlePlaceSelect = useCallback((place: Place) => {
    setSelectedPlace(place);
    if (window.innerWidth <= 768) {
      setIsListVisible(false);
    }
  }, []);

  const handleShowNearby = useCallback(() => {
    if (!navigator.geolocation) {
      alert('Geolocation is not supported by your browser');
      return;
    }

    setIsLoading(true);

    navigator.geolocation.getCurrentPosition(
      position => {
        const userLat = position.coords.latitude;
        const userLng = position.coords.longitude;

        // Центрируем карту по координатам пользователя
        if (mapRef.current) {
          mapRef.current.setView([userLat, userLng], 16, {
            animate: true,
            duration: 0.5
          });

          // Устанавливаем маркер с кастомной иконкой
          const userMarker = L.marker([userLat, userLng], {
            icon: L.icon({
              iconUrl: '/data/images/pin.svg',
              iconSize: [30, 40],
              iconAnchor: [15, 40]
            })
          }).addTo(mapRef.current);

          // Очищаем предыдущий активный маркер, если он есть
          if (activeMarkerRef.current) {
            mapRef.current.removeLayer(activeMarkerRef.current);
          }
          activeMarkerRef.current = userMarker;
        }

        setIsLoading(false);
      },
      error => {
        console.error('Error getting location:', error);
        alert('Unable to get your location');
        setIsLoading(false);
      }
    );
  }, [places]);

  if (!config) return null;

  return (
    <ThemeProvider theme={config.styleSettings}>
      <AppContainer>
        <Header config={config} />
        <Main>
          <Filters
            config={config}
            activeFilters={activeFilters}
            onFilterChange={handleFilterChange}
            filterCounts={filterCounts}
            toggleFilter={toggleFilter}
            isFilterAvailable={isFilterAvailable}
            resetFilters={handleReset}
          />
          <Content>
            <Map
              places={filteredPlaces}
              config={config}
              onMarkerClick={handlePlaceSelect}
              selectedPlace={selectedPlace}
              mapRef={mapRef}
            />
            <PlacesList
              places={filteredPlaces}
              config={config}
              onPlaceSelect={handlePlaceSelect}
              selectedPlace={selectedPlace}
              isVisible={isListVisible}
              onClose={() => setIsListVisible(false)}
            />
            <ShowListButton onClick={() => setIsListVisible(true)}>
              {config.content.buttonLabels.showList}
            </ShowListButton>
            <NearbyButton onClick={handleShowNearby}>
              {isLoading ? 'Loading...' : config.content.buttonLabels.showNearby}
            </NearbyButton>
          </Content>
        </Main>
      </AppContainer>
    </ThemeProvider>
  );
};

export default App;
