import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { ThemeProvider } from 'styled-components';
import { Header } from './components/Header/Header';
import { Map } from './components/Map/Map';
import { PlacesList } from './components/PlacesList/PlacesList';
import { Filters } from './components/Filters/Filters';
import { Place, AppConfig } from './types/places';
import { useUrlFilters } from './hooks/useUrlFilters';
import { styled } from 'styled-components';
import L from 'leaflet';

const AppContainer = styled.div`
  height: 100vh;
  display: flex;
  flex-direction: column;
`;

const Main = styled.main`
  margin: 56px 0 0;
  min-height: calc(100vh - 56px);
  display: flex;
  flex-direction: column;
  padding: 0;
`;

const Content = styled.div`
  position: relative;
  flex: 1;
  min-height: 0;
  padding: 0;
  display: flex;
`;

const ShowListButton = styled.button`
  position: absolute;
  top: 90px;
  right: 20px;
  padding: 8px 16px;
  background: ${props => props.theme.colors?.primary || '#333'};
  color: white;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  z-index: 20;
  display: none;

  @media (max-width: 768px) {
    display: block;
  }

  &:hover {
    background: ${props => props.theme.colors?.hover || '#555'};
  }
`;

const NearbyButton = styled.button`
  position: fixed;
  font-size: 18px;
  bottom: 100px;
  left: 50%;
  transform: translateX(-50%);
  padding: 15px 30px;
  background: #FF7F50;
  color: white;
  border: 0;
  border-radius: 50px;
  z-index: 9;
  cursor: pointer;
  font-weight: bold;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
  display: none;
  transition: opacity 0.3s ease, visibility 0.3s ease;

  @media (max-width: 768px) {
    display: block;
  }
`;

const App: React.FC = () => {
  const [config, setConfig] = useState<AppConfig | null>(null);
  const [places, setPlaces] = useState<Place[]>([]);
  const [activeFilters, setActiveFilters] = useState<string[]>([]);
  const [selectedPlace, setSelectedPlace] = useState<Place | null>(null);
  const [isListVisible, setIsListVisible] = useState(window.innerWidth > 768);
  const [isLoading, setIsLoading] = useState(false);
  const mapRef = React.useRef<L.Map | null>(null);
  const activeMarkerRef = React.useRef<L.Marker | null>(null);

  // –í—ã—á–∏—Å–ª—è–µ–º –æ—Ç—Ñ–∏–ª—å—Ç—Ä–æ–≤–∞–Ω–Ω—ã–µ –º–µ—Å—Ç–∞ —Å –ø–æ–º–æ—â—å—é useMemo
  const filteredPlaces = useMemo(() => {
    console.log('üìä –í—ã—á–∏—Å–ª—è–µ–º –æ—Ç—Ñ–∏–ª—å—Ç—Ä–æ–≤–∞–Ω–Ω—ã–µ –º–µ—Å—Ç–∞');
    console.log('üìä –ê–∫—Ç–∏–≤–Ω—ã–µ —Ñ–∏–ª—å—Ç—Ä—ã:', activeFilters);

    if (activeFilters.length === 0) {
      console.log('üìä –ù–µ—Ç –∞–∫—Ç–∏–≤–Ω—ã—Ö —Ñ–∏–ª—å—Ç—Ä–æ–≤, –≤–æ–∑–≤—Ä–∞—â–∞–µ–º –≤—Å–µ –º–µ—Å—Ç–∞:', places.length);
      return places;
    }

    const filtered = places.filter(place =>
      activeFilters.every(filter => place.attributes.includes(filter))
    );
    console.log('üìä –û—Ç—Ñ–∏–ª—å—Ç—Ä–æ–≤–∞–Ω–Ω—ã–µ –º–µ—Å—Ç–∞:', filtered.length);
    return filtered;
  }, [places, activeFilters]);

  // –í—ã—á–∏—Å–ª—è–µ–º –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ –º–µ—Å—Ç –¥–ª—è –∫–∞–∂–¥–æ–≥–æ —Ñ–∏–ª—å—Ç—Ä–∞ —Å –ø–æ–º–æ—â—å—é useMemo
  const filterCounts = useMemo(() => {
    console.log('\nüî¢ –ù–∞—á–∏–Ω–∞–µ–º –ø–æ–¥—Å—á–µ—Ç —Ñ–∏–ª—å—Ç—Ä–æ–≤');
    console.log('üî¢ –ê–∫—Ç–∏–≤–Ω—ã–µ —Ñ–∏–ª—å—Ç—Ä—ã:', activeFilters);

    const counts: Record<string, number> = {};
    const activeFilterSet = new Set(activeFilters);

    console.log('üî¢ –ü—Ä–æ—Ö–æ–¥–∏–º –ø–æ –≤—Å–µ–º –º–µ—Å—Ç–∞–º:', places.length);
    places.forEach(place => {
      console.log(`\nüè† –û–±—Ä–∞–±–∞—Ç—ã–≤–∞–µ–º –º–µ—Å—Ç–æ: ${place.name}`);
      console.log('üè† –ê—Ç—Ä–∏–±—É—Ç—ã –º–µ—Å—Ç–∞:', place.attributes);

      place.attributes.forEach(attr => {
        if (!counts[attr]) counts[attr] = 0;

        // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –ø–æ–¥—Ö–æ–¥–∏—Ç –ª–∏ –º–µ—Å—Ç–æ –ø–æ–¥ –¥—Ä—É–≥–∏–µ –∞–∫—Ç–∏–≤–Ω—ã–µ —Ñ–∏–ª—å—Ç—Ä—ã
        const otherFilters = Array.from(activeFilterSet).filter(f => f !== attr);
        console.log(`  üîç –ü—Ä–æ–≤–µ—Ä—è–µ–º –∞—Ç—Ä–∏–±—É—Ç "${attr}"`);
        console.log(`  üîç –î—Ä—É–≥–∏–µ –∞–∫—Ç–∏–≤–Ω—ã–µ —Ñ–∏–ª—å—Ç—Ä—ã:`, otherFilters);

        if (otherFilters.length === 0) {
          counts[attr]++;
          console.log(`  ‚úÖ –ù–µ—Ç –¥—Ä—É–≥–∏—Ö —Ñ–∏–ª—å—Ç—Ä–æ–≤, —É–≤–µ–ª–∏—á–∏–≤–∞–µ–º —Å—á–µ—Ç—á–∏–∫ –¥–ª—è ${attr}: ${counts[attr]}`);
        } else if (otherFilters.every(f => place.attributes.includes(f))) {
          counts[attr]++;
          console.log(`  ‚úÖ –ú–µ—Å—Ç–æ –ø–æ–¥—Ö–æ–¥–∏—Ç –ø–æ–¥ –≤—Å–µ —Ñ–∏–ª—å—Ç—Ä—ã, —É–≤–µ–ª–∏—á–∏–≤–∞–µ–º —Å—á–µ—Ç—á–∏–∫ –¥–ª—è ${attr}: ${counts[attr]}`);
        } else {
          console.log(`  ‚ùå –ú–µ—Å—Ç–æ –Ω–µ –ø–æ–¥—Ö–æ–¥–∏—Ç –ø–æ–¥ –¥—Ä—É–≥–∏–µ —Ñ–∏–ª—å—Ç—Ä—ã –¥–ª—è ${attr}, —Å—á–µ—Ç—á–∏–∫: ${counts[attr]}`);
        }
      });
    });

    console.log('\nüìà –ò—Ç–æ–≥–æ–≤—ã–µ —Å—á–µ—Ç—á–∏–∫–∏ —Ñ–∏–ª—å—Ç—Ä–æ–≤:', counts);
    return counts;
  }, [places, activeFilters]);

  useEffect(() => {
    // –ó–∞–≥—Ä—É–∑–∫–∞ –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–∏
    fetch('/data/config.json')
      .then(response => response.json())
      .then(data => setConfig(data))
      .catch(error => console.error('Error loading config:', error));

    // –ó–∞–≥—Ä—É–∑–∫–∞ –º–µ—Å—Ç
    fetch('/data/places.json')
      .then(response => response.json())
      .then(data => {
        setPlaces(data);
        // –î–µ–ª–∞–µ–º –º–µ—Å—Ç–∞ –¥–æ—Å—Ç—É–ø–Ω—ã–º–∏ –≥–ª–æ–±–∞–ª—å–Ω–æ –¥–ª—è useUrlFilters
        window.places = data;
      })
      .catch(error => console.error('Error loading places:', error));

    // –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –Ω–∞—á–∞–ª—å–Ω—É—é –≤–∏–¥–∏–º–æ—Å—Ç—å —Å–ø–∏—Å–∫–∞ –º–µ—Å—Ç
    const handleResize = () => {
      setIsListVisible(window.innerWidth > 768);
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∏–∑–º–µ–Ω–µ–Ω–∏—è —Ñ–∏–ª—å—Ç—Ä–æ–≤
  const handleFilterChange = useCallback((newFilters: string[]) => {
    console.log('üìç –ò–∑–º–µ–Ω–µ–Ω–∏–µ —Ñ–∏–ª—å—Ç—Ä–æ–≤:', newFilters);
    setActiveFilters(newFilters);
  }, []);

  // –ü–æ–ª—É—á–∞–µ–º —Ñ—É–Ω–∫—Ü–∏–∏ –¥–ª—è —Ä–∞–±–æ—Ç—ã —Å —Ñ–∏–ª—å—Ç—Ä–∞–º–∏
  const { toggleFilter, isFilterAvailable, resetFilters } = useUrlFilters(
    activeFilters,
    handleFilterChange,
    filterCounts
  );

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ —Å–±—Ä–æ—Å–∞ —Ñ–∏–ª—å—Ç—Ä–æ–≤
  const handleReset = useCallback(() => {
    resetFilters();
    setSelectedPlace(null); // –°–±—Ä–∞—Å—ã–≤–∞–µ–º –≤—ã–±—Ä–∞–Ω–Ω–æ–µ –º–µ—Å—Ç–æ
  }, [resetFilters]);

  const handlePlaceSelect = useCallback((place: Place) => {
    setSelectedPlace(place);
    if (window.innerWidth <= 768) {
      setIsListVisible(false);
    }
  }, []);

  const handleShowNearby = useCallback(() => {
    if (!navigator.geolocation) {
      alert('Geolocation is not supported by your browser');
      return;
    }

    setIsLoading(true);

    navigator.geolocation.getCurrentPosition(
      position => {
        const userLat = position.coords.latitude;
        const userLng = position.coords.longitude;

        // –¶–µ–Ω—Ç—Ä–∏—Ä—É–µ–º –∫–∞—Ä—Ç—É –ø–æ –∫–æ–æ—Ä–¥–∏–Ω–∞—Ç–∞–º –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
        if (mapRef.current) {
          mapRef.current.setView([userLat, userLng], 16, {
            animate: true,
            duration: 0.5
          });

          // –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –º–∞—Ä–∫–µ—Ä —Å –∫–∞—Å—Ç–æ–º–Ω–æ–π –∏–∫–æ–Ω–∫–æ–π
          const userMarker = L.marker([userLat, userLng], {
            icon: L.icon({
              iconUrl: '/data/images/pin.svg',
              iconSize: [30, 40],
              iconAnchor: [15, 40]
            })
          }).addTo(mapRef.current);

          // –û—á–∏—â–∞–µ–º –ø—Ä–µ–¥—ã–¥—É—â–∏–π –∞–∫—Ç–∏–≤–Ω—ã–π –º–∞—Ä–∫–µ—Ä, –µ—Å–ª–∏ –æ–Ω –µ—Å—Ç—å
          if (activeMarkerRef.current) {
            mapRef.current.removeLayer(activeMarkerRef.current);
          }
          activeMarkerRef.current = userMarker;
        }

        setIsLoading(false);
      },
      error => {
        console.error('Error getting location:', error);
        alert('Unable to get your location');
        setIsLoading(false);
      }
    );
  }, [places]);

  if (!config) return null;

  return (
    <ThemeProvider theme={config.styleSettings}>
      <AppContainer>
        <Header config={config} />
        <Main>
          <Filters
            config={config}
            activeFilters={activeFilters}
            onFilterChange={handleFilterChange}
            filterCounts={filterCounts}
            toggleFilter={toggleFilter}
            isFilterAvailable={isFilterAvailable}
            resetFilters={handleReset}
          />
          <Content>
            <Map
              places={filteredPlaces}
              config={config}
              onMarkerClick={handlePlaceSelect}
              selectedPlace={selectedPlace}
              mapRef={mapRef}
            />
            <PlacesList
              places={filteredPlaces}
              config={config}
              onPlaceSelect={handlePlaceSelect}
              selectedPlace={selectedPlace}
              isVisible={isListVisible}
              onClose={() => setIsListVisible(false)}
            />
            <ShowListButton onClick={() => setIsListVisible(true)}>
              {config.content.buttonLabels.showList}
            </ShowListButton>
            <NearbyButton onClick={handleShowNearby}>
              {isLoading ? 'Loading...' : config.content.buttonLabels.showNearby}
            </NearbyButton>
          </Content>
        </Main>
      </AppContainer>
    </ThemeProvider>
  );
};

export default App;
